(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-11-07 13:17:03', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-11-07 13:17:03', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-07 13:17:04', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-11-07 13:17:04', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-11-07 13:17:04', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-08 13:16:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-08 13:16:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-14 16:38:39', NULL, 0, 'plain', '', 0, 1, 'info'),
(59, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protect your WooCommerce Store with Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2024-11-14 16:38:40', NULL, 0, 'thumbnail', 'http://localhost/wordpress/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 1, 'info'),
(60, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2024-11-16 16:50:57', NULL, 0, 'plain', '', 0, 1, 'info'),
(61, 'wc-update-db-reminder', 'update', 'en_US', 'WooCommerce database update required', 'WooCommerce has been updated! To keep things running smoothly, we have to update your database to the newest version. The database update process runs in the background and may take a little while, so please be patient. Advanced users can alternatively update via <a href=\"https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI\">WP CLI</a>.', '{}', 'unactioned', 'woocommerce-core', '2024-11-17 07:36:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'Renewal_notification_emails_Woosubscriptions_Nov2024', 'marketing', 'en_US', 'WooCommerce Subscriptions just got even better', 'You can now send automatic email reminders to customers before their subscriptions renew or expire. To enable this for your store, update to the latest version of WooCommerce Subscriptions (don\'t forget to make a backup first) and follow the steps linked below.', '{}', 'pending', 'woocommerce.com', '2024-11-19 15:15:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2024-12-09 10:05:44', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'usps-mail-service-to-canada-suspended-dec-2024', 'update', 'en_US', 'Please note: USPS Mail Service to Canada Suspended', 'The United States Postal Service has temporarily suspended mail service to Canada due to the ongoing Canadian postal workers\' strike. USPS is asking users to refrain from mailing items to Canada at this time.', '{}', 'pending', 'woocommerce.com', '2024-12-09 10:05:44', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2025-02-17 04:29:09', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-02-17 04:29:33', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-04-09 02:25:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'viva_wallet_migration_q1_2025', 'update', 'en_US', 'Switch to the new Viva.com Smart Checkout', 'The Viva Wallet extension is no longer being updated and improved. To get the bestViva.comexperience, move to the new Smart Checkout. Its fast, secure, and fully compatible with the latest WooCommerce features.', '{}', 'pending', 'woocommerce.com', '2025-04-09 02:25:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'stripe_orderstatus_bug_q2_2025', 'update', 'en_US', 'Action Required: Update Order Status Manager', 'Update to the latest Order Status Manager (v1.15.5+) to ensure payments are captured. Your version of WooCommerce Order Status Manager can interfere with order processing for payment gateways (including WooCommerce Stripe Gateway v9.2.0+), which may cause orders to process without capturing payment.', '{}', 'pending', 'woocommerce.com', '2025-04-09 02:25:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'wc-admin-manage-orders-on-the-go', 'info', 'en_US', 'Manage your orders on the go', 'Look for orders, customer info, and process refunds in one click with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2025-08-17 17:56:35', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'wc-admin-performance-on-mobile', 'info', 'en_US', 'Track your store performance on mobile', 'Monitor your sales and high performing products with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2025-08-17 17:56:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-08-17 17:56:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-08-17 17:56:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-08-17 17:56:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(75, 'stripe_optimizedcheckout_upgrade_q3_2025', 'marketing', 'en_US', 'Upgrade for a faster, more flexible Stripe checkout', 'You\'re not on the latest WooCommerce Stripe Payment Gateway version. Upgrade to version 9.8 to access Stripe\'s Optimized Checkout Suite and leverage AI models to dynamically surface the most relevant payment methods for each customer, including BNPL, local options, and one-click Link checkout.', '{}', 'pending', 'woocommerce.com', '2025-08-17 17:56:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(76, 'stripe_optimizedcheckout_enable_q3_2025', 'marketing', 'en_US', 'Unlock dynamic payments and sell more', 'Youre on the latest WooCommerce Stripe Payment Gateway  now unlock its full potential by enabling the Optimized Checkout Suite. It uses AI models to dynamically surface the most relevant payment methods for each customer, helping you boost conversions.', '{}', 'pending', 'woocommerce.com', '2025-08-17 17:56:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(77, 'stripe_optimizedcheckout_promotion_q3_2025', 'marketing', 'en_US', 'Upgrade to Stripe for a faster, more flexible checkout', 'Unlock dynamic payments with Stripes Optimized Checkout Suite  now available with the WooCommerce Stripe Payment Gateway. Leverage AI models to dynamically surface the most relevant payment methods for each customer, including BNPL, local options, and one-click Link checkout.', '{}', 'pending', 'woocommerce.com', '2025-08-17 17:56:56', NULL, 0, 'plain', '', 0, 0, 'info');
